mapper.update:
update cardinfo
set name = #{name,jdbcType=VARCHAR},
  price = #{price,jdbcType=DOUBLE},
  carddesc = #{carddesc,jdbcType=VARCHAR},
  discount = #{discount,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from cardinfo ,muser u
where u.cardid = id and u.id = #{userId}

mapper.update:
update muser
set cardid = #{id}
where id = #{userId}
